mapper.select:
SELECT
appeal_id,user_id,user_name,appeal_title,phone,appeal_content,province,city,district,
full_address,appeal_more_info,sou_coin,latitude,longitude,
appeal_status,start_time,end_time
FROM
tb_appeal

	appeal_status = 0


		and appeal_title like '%${appealTitle}%'


		and latitude <= #{maxlat}


		and latitude >= #{minlat}


		and longitude <= #{maxlng}


		and longitude >= #{minlng}


ORDER BY start_time desc;

SELECT
tb_appeal.user_name,appeal_id,tb_appeal.user_id,appeal_title,tb_appeal.phone,appeal_content,province,city,district,
full_address,appeal_more_info,tb_appeal.sou_coin,latitude,longitude,
appeal_status,start_time,end_time
FROM
tb_appeal
LEFT JOIN tb_person_info
ON tb_appeal.user_id=tb_person_info.user_id


		and appeal_title like '%${appealCondition.appealTitle}%'


		and appeal_content like '%${appealCondition.appealContent}%'


		and appeal_status = #{appealCondition.appealStatus}


		and province = #{appealCondition.province}


		and tb_appeal.phone = #{appealCondition.phone}


		and city = #{appealCondition.city}


		and district = #{appealCondition.district}


		and tb_appeal.user_id = #{appealCondition.userId}


		and appeal_id = #{appealCondition.appealId}


		and start_time >= #{appealCondition.startTime}


		and end_time <= #{appealCondition.endTime}


		and tb_appeal.sou_coin >= #{appealCondition.souCoin}




	ORDER BY ${sort} ${order}


	ORDER BY start_time desc

LIMIT #{rowIndex},#{pageSize};

SELECT
appeal_id,appeal_title,phone,appeal_content,province,city,district,
full_address,appeal_more_info,sou_coin,latitude,longitude,
appeal_status,start_time,end_time
FROM
tb_appeal


		and appeal_title like '%${appealCondition.appealTitle}%'


		and appeal_status = #{appealCondition.appealStatus}


		and province = #{appealCondition.province}


		and city = #{appealCondition.city}


		and district = #{appealCondition.district}


		and user_id = #{appealCondition.userId}


ORDER BY start_time desc;

mapper.update:
update tb_appeal
appeal_title=#{appealTitle}, phone=#{phone}, appeal_content=#{appealContent}, province=#{province}, city=#{city}, district=#{district}, full_address=#{fullAddress}, appeal_more_info=#{appealMoreInfo}, sou_coin=#{souCoin}, latitude=#{latitude}, longitude=#{longitude}, appeal_status=#{appealStatus}, start_time=#{startTime}, end_time=#{endTime}
where appeal_id=#{appealId}
