mapper.select:
SELECT user_name, help_id, appeal_id, tb_help.user_id, appeal_title, help_status, completion, efficiency, attitude, avg_completion, avg_efficiency, avg_attitude, all_coin, additional_coin, end_time, comment_info FROM tb_help LEFT JOIN tb_person_info ON tb_help.user_id=tb_person_info.user_id
and appeal_id = #{helpCondition.appealId}
and tb_help.user_id = #{helpCondition.userId}
and help_status = #{helpCondition.helpStatus}
and (end_time >= #{startTime} and end_time <= #{endTime})
and additional_coin = #{helpCondition.additionalCoin}
and all_coin = #{helpCondition.allCoin}
and help_id = #{helpCondition.helpId}
and appeal_title like '%${helpCondition.appealTitle}%'
and comment_info like '%${helpCondition.commentInfo}%'
ORDER BY ${sort} ${order}
ORDER BY help_id desc
LIMIT #{rowIndex},#{pageSize};

SELECT help_id, appeal_id, appeal_title, help_status, completion, efficiency, attitude, avg_completion, avg_efficiency, avg_attitude, all_coin, additional_coin, end_time, comment_info FROM tb_help
and appeal_id = #{helpCondition.appealId}
and user_id = #{helpCondition.userId}
and help_status = #{helpCondition.helpStatus}

mapper.update:
update tb_help
help_status=#{helpStatus}, completion=#{completion}, efficiency=#{efficiency}, attitude=#{attitude}, all_coin=#{allCoin}, additional_coin=#{additionalCoin}, comment_info=#{commentInfo}
where help_id=#{helpId}
