mapper.select:
SELECT user_id, user_name, profile_img, sex, birth, phone, sou_coin, user_type, email, real_name, identification_number, create_time, last_edit_time, enable_status FROM tb_person_info
and user_name like '%${personInfoCondition.userName}%'
and enable_status = #{personInfoCondition.enableStatus}
and user_id = #{personInfoCondition.userId}
and phone = #{personInfoCondition.phone}
and email = #{personInfoCondition.email}
ORDER BY ${sort} ${order}
ORDER BY create_time desc
LIMIT #{rowIndex},#{pageSize};

mapper.update:
update tb_person_info
user_name=#{userName}, password=#{password}, profile_img=#{profileImg}, sex=#{sex}, birth=#{birth}, phone=#{phone}, sou_coin=#{souCoin}, user_type=#{userType}, email=#{email}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus}, profile_img=#{profileImg}
where user_id=#{userId}
